(function() {
  'use strict';

  angular
    .module('test', [
      'ui.router',
      'test.nav',
      'test.main',
      'test.phones',
      'test.feedback'
    ])
    .config(Config);

  Config.$inject = ['$stateProvider', '$urlRouterProvider', '$locationProvider'];

  function Config($stateProvider, $urlRouterProvider, $locationProvider) {
    $urlRouterProvider.otherwise('/main');
    $stateProvider
      .state('root', {
        abstract: true,
        views: {
          '@': {
            templateUrl: '../views/root.html'
          },
          'navbar@root': {
            template: '<navigation></navigation>'
          }
        }
      })

    $locationProvider.html5Mode({
      enabled: true,
      requireBase: false
    });
  };

})();
(function() {
  'use strict';

  angular
    .module('test.nav', [])
    .config([config])
    .controller('NavCtrl', NavCtrl)
    .directive('navigation', function() {
      return {
        restrict: 'E',
        templateUrl: '/views/navigation.html',
        replace: true,
        controller: NavCtrl
      }
    });

  NavCtrl.$inject = ['$scope', '$location'];

  function NavCtrl($scope, $location) {
    var ctrl_this = this;
    ctrl_this.nav_items = [{
        name: 'Main',
        url: 'root.main'
      },
      {
        name: 'Phones',
        url: 'root.phones'
      },
      {
        name: 'feedback',
        url: 'root.feedback'
      }
    ];
  };

  function config() {};
})();
(function() {
  'use strict';

  angular
    .module('test.feedback', ['ui.router'])
    .config(['$stateProvider', config])
    .controller('FeedbackCtrl', FeedbackCtrl);

  FeedbackCtrl.$inject = ['$scope', '$rootScope'];

  function FeedbackCtrl($scope, $rootScope) {
    $rootScope.pageTitle = 'Test - Feedback';
    $scope.submitForm = function() {
      console.log(JSON.stringify($scope.form));
      $scope.form = {
        userName: null,
        userText: null
      };
    }
  };

  function config($stateProvider) {
    $stateProvider
      .state('root.feedback', {
        url: '/feedback',
        templateUrl: '../views/pages/feedback.html'
      })
  };

})();
(function() {
  'use strict';

  angular
    .module('test.main', ['ui.router'])
    .config(['$stateProvider', config])
    .controller('MainCtrl', MainCtrl);

  MainCtrl.$inject = ['$scope', '$rootScope'];

  function MainCtrl($scope, $rootScope) {
    $rootScope.pageTitle = 'Test - Main';
  };

  function config($stateProvider) {
    $stateProvider
      .state('root.main', {
        url: '/main',
        templateUrl: '../views/pages/main.html',
        controller: MainCtrl
      })
  };
  
})();
(function() {
  'use strict';

  angular
    .module('test.phones', ['ui.router'])
    .config(['$stateProvider', '$urlRouterProvider', config])
    .controller('PhonesCtrl', PhonesCtrl);

  PhonesCtrl.$inject = ['$scope', '$rootScope', '$http'];

  function PhonesCtrl($scope, $rootScope, $http) {
    $rootScope.pageTitle = 'Test - Phones';

    $scope.items = [{
        name: 'Nokia',
        url: 'nokia'
      },
      {
        name: 'HTC',
        url: 'htc'
      },
      {
        name: 'Apple',
        url: 'apple'
      }
    ];

    $http({
      method: 'get',
      url: '../json/phones.json'
    }).then(function(response) {
      var nokia = response.data.nokia;
      var htc = response.data.htc;
      var apple = response.data.apple;
      $scope.nokiaData = nokia;
      $scope.htcData = htc;
      $scope.appleData = apple;
    }, function(error) {
      console.log(error);
    });

  };

  function config($stateProvider, $urlRouterProvider) {
    $urlRouterProvider.otherwise('/main');
    $stateProvider
      .state('root.phones', {
        url: '/phones',
        templateUrl: '../views/pages/phones/phones.html',
        controller: PhonesCtrl
      })
      .state('nokia', {
        url: '/nokia',
        parent: 'root.phones',
        templateUrl: '../views/pages/phones/phones.nokia.html',
        authenticate: true
      })
      .state('htc', {
        url: '/htc',
        parent: 'root.phones',
        templateUrl: '../views/pages/phones/phones.htc.html'
      })
      .state('apple', {
        url: '/apple',
        parent: 'root.phones',
        templateUrl: '../views/pages/phones/phones.apple.html'
      })
  };

})();